import json
from langflow.base.io.text import TextComponent
from langflow.io import MultilineInput, Output
from langflow.schema.message import Message

class TextInputComponent(TextComponent):
    display_name = "Text Input"
    icon = "type"

    inputs = [MultilineInput(name="input_value", display_name="Text")]
    outputs = [
        Output(display_name="User Message",     name="user_message",    method="user_message_response"),
        Output(display_name="Thread History",   name="thread_history",  method="thread_history_response"),
        Output(display_name="Files JSON",       name="files",           method="files_response"),
        Output(display_name="Slack Bot Token",  name="slack_bot_token", method="slack_bot_token_response"),
        Output(display_name="Thread ID",       name="thread_ts",        method="thread_ts_response"),
        Output(display_name="Channel ID",       name="channel_id",      method="channel_id_response"),
    ]

    # ---------- helpers ----------
    def _parsed(self) -> dict:
        """Return a dict even if the string isn't valid JSON."""
        raw = self.input_value or ""
        try:
            return json.loads(raw.strip())
        except json.JSONDecodeError:
            # fall back to newline-separated format: first line, second line, third line
            parts = [p.strip() for p in raw.splitlines() if p.strip()]
            keys  = ("user_message", "thread_history", "files", "slack_bot_token", "channel_id", "thread_ts")
            return {k: parts[i] if i < len(parts) else "" for i, k in enumerate(keys)}

    # ---------- outputs ----------
    def user_message_response(self) -> Message:
        return Message(text=self._parsed().get("user_message", ""))

    def thread_history_response(self) -> Message:
        return Message(text=self._parsed().get("thread_history", ""))

    def files_response(self) -> Message:
        return Message(text=self._parsed().get("files", ""))

    def slack_bot_token_response(self) -> Message:
        return Message(text=self._parsed().get("slack_bot_token", ""))

    def channel_id_response(self) -> Message:
        return Message(text=self._parsed().get("channel_id", ""))

    def thread_ts_response(self) -> Message:
        return Message(text=self._parsed().get("thread_ts", ""))

import requests
from langflow.base.io.text import TextComponent
from langflow.io import MultilineInput, Output
from langflow.schema.message import Message
from io import BytesIO
from PyPDF2 import PdfReader


class TextInputComponent(TextComponent):
    display_name = "PDF File Extractor"
    description = "Extracts the content from uploaded PDF Files"
    icon = "file"
    name = "PDFFileExtractor"

    inputs = [
        MessageTextInput(
            name="files",
            display_name="Files JSON"
        ),
        MessageTextInput(
            name="slack_bot_token",
            display_name="Slack Bot Token"
        ),
    ]
    outputs = [
        Output(display_name="Message", name="text", method="text_response"),
    ]

    def text_response(self) -> Message:
        extracted_text = ""
        if self.files != "":
            files = json.loads(self.files)
            for file_info in files:
                file_url = file_info.get("url_private")
                mimetype = file_info.get("mimetype")
                filename = file_info.get("name")

                print(f"üìé Processing file: {filename}")
                try:
                    headers = {"Authorization": "Bearer {SLACK_BOT_TOKEN}".format(SLACK_BOT_TOKEN=self.slack_bot_token)}
                    r = requests.get(file_url, headers=headers)
                    file_bytes = BytesIO(r.content)

                    if "pdf" in mimetype:
                        reader = PdfReader(file_bytes)
                        for page in reader.pages:
                            extracted_text += page.extract_text() or ""
                except Exception as e:
                    print(f"‚ùå Failed to extract file {filename}:", e)
        return Message(
            text=extracted_text,
        )
